<script setup lang="ts">
type SvgEvent = {
  element: SVGElement,
  x: number,
  y: number,
}

const emit = defineEmits<{ (event: 'hover', e: SvgEvent | undefined): void }>();

const onHover = (event: MouseEvent) => {
  const element = event.target as SVGElement;
  const { clientX: x, clientY: y } = event;
  emit('hover', { element, x, y });
};

const onMouseOut = () => emit('hover', undefined);
</script>
